mapper.select:
select C.*, U.username, U.nickname, U.avatar
from blog_comment as C
         left join user as U on C.user_id = U.id
where C.article_id = #{articleId}
order by C.update_time
limit #{limit} offset #{offset};

select *
from blog_comment
where article_id = #{articleId}
order by rand()
limit #{N}
